INSERT INTO `settings` (`id`, `key`, `display_name`, `value`, `details`, `type`, `order`, `group`) VALUES (1,'site.title','Site Title','Taxi Free','','text',1,'Site'),(2,'site.description','Site Description','FREE TAXI არის ტაქსის გამოძახების აპლიკაცია,      რომელიც გამოიყენება თქვენი კომფორტისთვის.      FREE TAXI-ს გამოძახება შესაძლებელია 24/7.      შეგიძლიათ იხილოთ თქვენი მგზავრობის ფასი შეკვეთის გაფორმებამდე.','','text',2,'Site'),(3,'site.logo','Site Logo','settings/October2020/GzEt2CS2pGMduXrrJxop.png','','image',3,'Site'),(4,'site.google_analytics_tracking_id','Google Analytics Tracking ID','G-5CBFX1N3VX','','text',4,'Site'),(5,'admin.bg_image','Admin Background Image','settings/May2020/dAIBiUIEvdhyW1sBvreg.jpg','','image',5,'Admin'),(6,'admin.title','Admin Title','Taxi Free','','text',1,'Admin'),(7,'admin.description','Admin Description','Welcome to Fრიი TAXI','','text',2,'Admin'),(8,'admin.loader','Admin Loader','settings\\April2020\\ANePGRg7K6sIh8IDzi4z.png','','image',3,'Admin'),(9,'admin.icon_image','Admin Icon Image','settings\\April2020\\W21qTVUrbIo9Dzr9bMIe.png','','image',4,'Admin'),(10,'admin.google_analytics_client_id','Google Analytics Client ID (used for admin dashboard)',NULL,'','text',1,'Admin'),(12,'site.About_Us','About US','<p><strong>FREE TAXI</strong> არის ტაქსის გამოძახების აპლიკაცია, რომელიც გამოიყენება თქვენი კომფორტისთვის. FREE TAXI-ს გამოძახება შესაძლებელია 24/7. შეგიძლიათ იხილოთ თქვენი მგზავრობის ფასი შეკვეთის გაფორმებამდე. ჩვენი მისიაა თქვენი უსაფრთხო და კომფორტული მგზავრობა.<br />ნაღდი და უნაღდო ანგარიშსწორება.<br />შეღავათიანი ფასები კომფორტული მგზავრობისთვის.<br />უსაფრთხო და სანდო.</p>',NULL,'rich_text_box',6,'Site'),(13,'site.privacy_policy','Privacy-Policy','<p>მონაცემთა კონფიდენციალობის შესახებ განცხადება შპს 20-20 (ს/კ 445575627) FREE TAXI აპლიკაციის მფლობელი ბოლო განახლების თარიღი: 2020 წლის 23.04.2020<br />_______________________________________________________________________________________________________________&nbsp;</p